import React from "react";
import {connect} from "react-redux";
import widgetService, {deleteWidget} from "../services/WidgetService"
import HeadingWidget from "./HeadingWidget";
import ParagraphWidget from "./ParagraphWidget";
import {Button} from "react-bootstrap";
const WidgetList = (
    {
      widgets = [],
        topicId,
        createWidgetForTopic
    }) =>
<div>
  <h3>Widgets</h3>
  <ul>
    {
      widgets.map(widget =>
      <div key ={widget.id}>
        {
          widget.type === "HEADING" &&
              <div className="wbdv-highlight-background-dark-selected">
              <HeadingWidget widget={widget}/>
              <Button onClick={() => deleteWidget(topicId, widget)}>
                Delete
              </Button>
              </div>
        }
        {
          widget.type === "PARAGRAPH" &&
              <div className="wbdv-highlight-background-dark-selected">
          <ParagraphWidget widget={widget}/>
                <Button className="btn btn-danger" onClick={() => deleteWidget(topicId, widget)}>
                  Delete
                </Button>
              </div>

        }
      </div>)
    }
  </ul>
  <button onClick={() => createWidgetForTopic(topicId)}>
    +
  </button>
</div>

const stateToPropMapper = (state) => ({
  widgets: state.widgetReducer.widgets,
  topicId: state.widgetReducer.topicId
})

const dispatchMapper = (dispatch) => ({
  createWidgetForTopic: (topicId) =>
    widgetService.createWidgetForTopic(topicId, {
      name: "New Widget",
      type: "PARAGRAPH"
    }).then(widget => dispatch({
      type: "CREATE_WIDGET_FOR_TOPIC",
      widget
    })),
  deleteWidgetForTopic: (topicId, widget) =>
    widgetService.deleteWidget(topicId, widget)
    .then(widget => dispatch({
      type: "DELETE_WIDGET_FOR_TOPIC",
      widget
    })),

  updateParagraphPreview: (topicId, widget) =>
    dispatch({
      type: "UPDATE_PREVIEW",
      widget
    })

})


export default connect(stateToPropMapper, dispatchMapper)(WidgetList)
