import React from "react";
import {Button} from "react-bootstrap";
import widgetService from "../services/WidgetService";
import {connect} from "react-redux";


const handleKeyPress = (event, {
  widget,
  updateParagraphPreview
}) => {
  var lines = event.target.value.split("\n")
  let text = "";
  for(var i = 0;i < lines.length;i++){
    if (i === lines.length - 1 && event.key !== "Enter"){
      text += `${lines[i]}${event.key}&list00 `
    } else {
      text += `${lines[i]}&list00 `
    }
  }

  console.log(text)
  updateParagraphPreview(widget, text)

  // if(event.key === 'Enter'){
  //   //alert("enter pressed")
  //   updateParagraphPreview(widget, `${event.target.value} \n`)
  // } else {
  //   updateParagraphPreview(widget, event.target.value)
  // }
}

const createList = (widget) => {
  if (widget.text !== null) {
    var lines = widget.text.split("&list00 ")
    return <ol>
      {lines.map(item => <li>{item}</li>)}
    </ol>
  }
}


const ListWidget = ({
  widget,
  updateParagraphPreview
}) =>
  <div>
    <h3>List Widget</h3>
    <div>
      <textarea className="form-control" onKeyPress={e => handleKeyPress(e, {widget, updateParagraphPreview} )}>
        {widget.text}
      </textarea>

      <h4>Preview</h4>
      {
        createList(widget)
      }

      <p>{widget.text}</p>
    </div>

  </div>


  export default ListWidget
