import React from "react";
import CourseRowComponent from "./CourseRowComponent";
import courseService from "../services/CourseService";
import {library} from '@fortawesome/fontawesome-svg-core';
import {fontAwesomeIcon} from "@fortawesome/fontawesome-svg-core";
import {faFileAlt, fas, faBars, faPlusCircle} from "@fortawesome/free-solid-svg-icons";
import "./course-list.style.client.css";
import 'bootstrap/dist/css/bootstrap.min.css';


library.add (
    faFileAlt,
    fas,
    faBars,
    faPlusCircle
)
class CourseListComponent extends React.Component {
  state = {
    courses: []
  }


  componentDidMount() {
    courseService.findAllCourses()
    .then(courses => this.setState({
      courses: courses
    }))
  }

  createCourse = () => {
    const newCourse = {
      title: 'New Course',
      owner: 'me',
      lastUpdated: 'yesterday'
    }

    // UNSAFE:
    // const newState = {
    //   courses: [
    //     ...this.state.courses, newCourse
    //   ]
    // }
    //
    // this.setState(newState)

    // SAFE:

    courseService.createCourse(newCourse)
    .then(actualCourse => this.setState(function (prevState) {
          return {
            courses: [
              ...prevState.courses, actualCourse
            ]
          }
        })
    )
    .catch(error => {})
  }

  deleteCourse = (course) => {
    courseService.deleteCourse(course._id)
    .then(statu => this.setState(prevState => ({
      courses: prevState.courses.filter(c => c._id !== course._id)
    })))
  }

  render() {
    return(
        <body className="wbdv-page-background">
        <div className="container">
          <h1 className="wbdv-sticky-header">
            <div className="form-group row">

              <i className="fas fa-bars wbdv-padding-left-header"></i>
              <label htmlFor="headerSearch" className="wbdv-header-menu-icon">Course
                Manager</label>

              <input id="headerSearch" placeholder="New Course Title"
                     className="form-control col-sm-6 wbdv-header-search-bar"/>

              <i className="wbdv-add-course-header-styling fas fa-plus-circle wbdv-circle-add-style"></i>
              <i className="wbdv-bottom-right-fixed fas fa-plus-circle wbdv-circle-add-style"></i>
            </div>
          </h1>
          <table className="table form-group ">
            <thead>
            <tr>
              <th>Title</th>
              <th>Owner</th>
              <th>Last Modified</th>
              <th></th>
            </tr>
            </thead>

            <tbody className="wbdv-table-background">
            <tr>
              <td>
                <i className="fas fa-file-alt wbdv-file-icon-style"></i>
                <a href="../course-editor/course-editor.template.client.html"
                   className="wbdv-document-table-text-style wbdv-course-editor wbdv-close">
                  CS4550
                </a>
              </td>
              <td>me</td>
              <td className>6:45 PM</td>
              <td><i className="fas fa-times"></i></td>
            </tr>
            <tr>
              <td>
                <i className="fas fa-file-alt wbdv-file-icon-style"></i>
                <a href="../course-editor/course-editor.template.client.html"
                   className="wbdv-document-table-text-style wbdv-course-editor wbdv-close">
                  CS4550
                </a>
              </td>
              <td>me</td>
              <td>6:45 PM</td>
              <td><i className="fas fa-times"></i></td>
            </tr>
            </tbody>
          </table>
        </div>
        </body>
    )
  }
}

export default CourseListComponent
