import React from "react";
import {connect} from "react-redux";
import lessonService from "../services/LessonService"
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faCheck, faPen, faTimes} from "@fortawesome/free-solid-svg-icons";
const LessonTabs = (
    {
      lessons=[],
      deleteLesson,
      createLessonForModule,
      moduleId,
      updateLesson,
      saveLesson
    }) =>
    <div>
      <h1>Lessons ({lessons.length})</h1>
      <ul>
        {
          lessons.map(lesson =>
              <li key={lesson._id}>
                <button onClick={() => deleteLesson(lesson._id)}>
                  <FontAwesomeIcon icon={faTimes}/>
                </button>
                <button onClick={() => updateLesson({...lesson, editing: true})}>
                  <FontAwesomeIcon icon={faPen}/>
                </button>
                {
                  lesson.editing === true &&
                  <button
                      onClick={() => updateLesson({...lesson, editing: false})}>
                    <FontAwesomeIcon icon={faCheck}/>
                  </button>
                }
                {
                  !lesson.editing &&
                  <span>{lesson.title}</span>
                }
                {
                  lesson.editing &&
                  <input
                      onChange={(e) => updateLesson({...lesson, title: e.target.value})}
                      value={lesson.title}/>
                }
                <button onClick={() => saveLesson(lesson)}>
                  Save
                </button>
              </li>
          )
        }
      </ul>
      <button onClick={() =>
          createLessonForModule(moduleId)}>
        Create
      </button>
    </div>

const stateToPropertyMapper = (state) => ({
  lessons: state.lessonReducer.lessons,
  moduleId: state.lessonReducer.moduleId
})

const dispatchToPropertyMapper = (dispatch) => ({
  saveLesson: (lesson) =>
      lessonService.saveLesson(lesson)
      .then(status => dispatch({
        type: "SAVE_LESSON",
        lesson
      })),
  deleteLesson: (lessonId) =>
      lessonService.deleteLesson(lessonId)
      .then(status => dispatch({
        type: "DELETE_LESSON",
        lessonId
      })),
  createLessonForModule: (moduleId) =>
      lessonService
      .createLessonForModule(moduleId, {title: "New Lesson"})
      .then(lesson => dispatch({
        type: "CREATE_LESSON",
        lesson
      })),
  updateLesson: (lesson) => dispatch({
    type: "UPDATE_LESSON",
    lesson
  })
})

export default connect
(stateToPropertyMapper, dispatchToPropertyMapper)
(LessonTabs)
