const widgetReducer = (state ={}, action) => {
    switch(action.type) {
      case "findWidgetsForTopic" :
        return {
          ...state,
          widgets: action.widgets,
          topicId: action.topicId
        }
      default:
        return state
    }
}

export default widgetReducer
