const stateToPropertyMapper = state => ({
  lessons: state.lessonReducer.lessons,
  moduleId: state.lessonReducer.moduleId
});

const dispatchToPropertyMapper = dispath => ({
  saveLesson: lesson => lessonService.saveLesson(lesson),
  deleteLesson: lessonId => lessonService.deleteLesson(lessonId).then(status => dispath({
    type: "DELETE_LESSON",
    lessonId
  })),
  createLessonForModule: moduleId => lessonService.createLessonForModule(moduleId, {
    title: "New Lesson"
  }).then(lesson => dispath({
    type: "CREATE_LESSON",
    lesson
  })),
  updateLesson: lesson => dispath({
    type: "UPDATE_LESSON",
    lesson
  })
});

export default connect(stateToPropertyMapper, dispatchToPropertyMapper)(LessonTabs);
