import React from "react";
import courseService, {updateCourse} from "../services/CourseService";
import {Link} from "react-router-dom";
import {faCheck, faFileAlt, faTrash, faPencilAlt} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import "./styles.css";
import moduleService, {updateModule} from "../services/ModuleService";

class ModuleComponent extends React.Component {
  state = {
    editing: false,
    moduleTitle: "Some Course",
    module: this.props.module,
    highlight: false
  }

  constructor(props) {
    super(props)
  }

  updateTitle = (event) => {
    const newTitle = event.target.value
    const course = {...this.state.course}
    course.title = newTitle
    this.setState({
      course: course
    })
  }

  updateModule = () => {
    //debugger
    this.setState({editing: false})
    updateModule(this.state.module._id, this.state.module)
  }


  render() {
    return (
        <h3>Module</h3>
    )
  }
}

export default ModuleComponent
