const stateToPropMapper = state => ({
  widgets: state.widgetReducer.widgets,
  topicId: state.widgetReducer.topicId
});

const dispatchMapper = dispatch => ({
  createWidgetForTopic: (topicId, type) => widgetService.createWidgetForTopic(topicId, {
    name: "New Widget",
    type: type
  }).then(widget => dispatch({
    type: "CREATE_WIDGET_FOR_TOPIC",
    widget
  })),
  deleteWidgetForTopic: (topicId, widget) => widgetService.deleteWidget(topicId, widget).then(widget => dispatch({
    type: "DELETE_WIDGET_FOR_TOPIC",
    widget
  })),
  saveWidgetForTopicDispatch: (topicId, widget) => widgetService.saveWidgetForTopic(topicId, widget).then(widget => dispatch({
    type: "SAVE_WIDGET_FOR_TOPIC",
    widget
  })),
  updateParagraphPreview: (widget, e) => {
    dispatch({
      type: "UPDATE_PARAGRAPH_PREVIEW",
      widget,
      e
    });
  },
  updateHeadingPreview: (widget, e) => {
    dispatch({
      type: "UPDATE_HEADING_PREVIEW",
      widget,
      e
    });
  }
});

export default connect(stateToPropMapper, dispatchMapper)(WidgetList);
