import React from "react";
import '../css/course-card.style.client.css'
import { Link } from "react-router-dom";

class CourseCardComponent extends React.Component {
  state = {
    editing: false,
    updatedCourseTitle: "",
    selected: false
  }

  updateForm = (newState) => {
    this.setState(newState)
  }

  render() {
    return (
        <div className="card col-xs-12 col-sm-6 col-md-4 col-lg-3 col-xl-2" onClick={() =>
            this.updateForm({
              selected: !this.state.selected
            })}
             style={{ 'backgroundColor': this.state.selected ? '#8491fb' : 'white' }}>
          <img className="card-img-top" alt="" src="img/card-img.png" />
          {
            !this.state.editing &&
            <div className="card-body">
              <div className="row">
                <Link to={`/course-editor/${this.props.course._id}`} className="course-title">
                  {this.props.course.title}
                </Link>
              </div>
              <div className="row">
                <i className="course-info">Modified Just now</i>
              </div>
              <div className="row">
                <button className="btn btn-block btn-edit-course" type="button" onClick={() => {
                  this.setState({
                    editing: true,
                    updatedCourseTitle: this.props.course.title
                  })
                }}>
                  <img alt="" src="img/edit.svg" className="edit-icon"></img>
                  Rename
                </button>
              </div>
              <div className="row">
                <button className="btn btn-block btn-delete-course" type="button" onClick={() => this.props.deleteCourse(this.props.course)}>
                  <img alt="" src="img/x.svg" className="delete-icon"></img>
                  Delete
                </button>
              </div>
            </div>
          }
          {
            this.state.editing &&
            <div className="card-body">
              <div className="row">
                <input type="text" id="edit-course-name" className="input-lg wbdv-field"
                       aria-describedby="widget-input" onChange={(e) => {
                  this.updateForm({
                    updatedCourseTitle: e.target.value
                  });
                }}></input>
              </div>
              <div className="row">
                <i className="course-info">Modified Just now</i>
              </div>
              <div className="row">
                <div className="col-sm-6">
                  <button className="btn btn-block btn-save-course" type="button" onClick={() => {
                    this.props.updateCourse({
                      title: this.state.updatedCourseTitle,
                      nuid: this.props.course.nuid,
                      domain: this.props.course.domain,
                      _id: this.props.course._id
                    });
                    this.setState({
                      editing: false
                    });
                  }}>
                    <img alt="" src="img/save.svg" className="save-icon"></img>
                    Save
                  </button>
                </div>
                <div className="col-sm-6">
                  <button className="btn btn-block btn-cancel-course" type="button" onClick={() => {
                    this.setState({
                      editing: false
                    })
                  }}>
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          }

        </div >
    )
  }
}

export default CourseCardComponent
