import React from "react";
import CourseRowComponent from "./CourseRowComponent";
import {findAllCourses, deleteCourse} from "../services/CourseService";


class CourseListComponent extends React.Component {
  state = {
    courses: [
      {
        _id:'123',
        title: 'CS4550',
        owner: 'me',
        lastUpdated: 'yesterday'
      },
      {
        _id:'345',
        title: 'CS5610',
        owner: 'me',
        lastUpdated: 'yesterday'
      },
      {
        _id:'456',
        title: 'CS3200',
        owner: 'me',
        lastUpdated: 'yesterday'
      }
    ]
  }
  //initialize the state
  componentDidMount() {
    // get the courses and store
    findAllCourses()
    .then(courses => this.setState( {
      courses: courses
    }))
  }

  createCourse = () => {
    const newCourse = {

      _id: (new Date()).getMilliseconds() + "",
      title: 'CS3200',
      owner: 'me',
      lastUpdated: 'yesterday'

    }

    // UNSAFE
    // const newState = {
    //   courses: [
    //       ...this.state.courses, newCourse
    //   ]
    // }
    // this.setState(newState)

    // SAFE
    this.setState(function (prevState) {
      return {
        courses: [
          ...prevState.courses, newCourse
        ]
      }
    })
  }

  deleteCourse = (course) => {
    deleteCourse(course._id)
    .then(statu => this.setState())
//alert(""+course.title)
    this.setState( prevState => ({

        courses: prevState.courses.filter(c => c._id !== course._id)

    }))
  }

  render() {
    return (<div className={"container"}>
      <button onClick={this.createCourse} className={"btn btn-success float-right"}>
        Create Course
      </button>
      <h1>Course List</h1>
      <table className={"table"}>
        <thead>
        <tr>
          <th>
            Title
          </th>
        </tr>
        </thead>
        <tbody>

        {
          this.state.courses.map(course =>
              <CourseRowComponent
                  deleteCourse = {this.deleteCourse}
                  course={course}/>
          )
        }
        </tbody>
      </table>
    </div>)
  }
}

export default CourseListComponent
