const stateToProperty = state => ({
  course: state.courseReducer.course
});

const propertyToDispatchMapper = dispatch => ({
  findLessonsForModule: moduleId => {
    lessonService.findLessonsForModule(moduleId).then(lessons => dispatch({
      type: "FIND_LESSONS_FOR_MODULE",
      lessons,
      moduleId
    }));
  },
  findModulesForCourse: courseId => moduleService.findModulesForCourse(courseId).then(actualModules => dispatch({
    type: "FIND_MODULES_FOR_COURSE",
    modules: actualModules
  })),
  findCourseById: courseId => findCourseById(courseId).then(actualCourse => dispatch({
    type: "FIND_COURSE_BY_ID",
    course: actualCourse
  }))
});

export default connect(stateToProperty, propertyToDispatchMapper)(CourseEditor);
