import React from "react";
import {connect} from "react-redux";
import "./styles.css";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {
  DELETE_MODULE,
  CREATE_MODULE,
  UPDATE_MODULE,
  updateModule,
  createModule,
  deleteModule
} from "../actions/moduleActions";
import {Link} from "react-router-dom";
import {faCheck, faTimes} from "@fortawesome/free-solid-svg-icons";
import ModuleComponent from "./ModuleComponent";
const highlightModuleParent = (moduleId) => {
  this.setState({selectedModuleId: moduleId})
}
const ModuleList = (
    { course,
      modules=[],
      deleteModule,
      createModule,
      updateModule}) =>
    <div>
      <h1>Modules for {course.title}</h1>
      <ul>
        {
          modules.map(module =>
              <ModuleComponent
                  key={module._id}
                  highlightModuleParent ={this.highlightModuleParent}
                  selectedModuleId ={this.state.selectedModuleId}
                  deleteModule={this.deleteModule}
                  module={module}/>
          )
        }
      </ul>
      <button onClick={() => createModule(course, {title: "New Module"})}>
        Create Module
      </button>
    </div>

// export default ModuleList

const stateToPropertyMapper = (state) => ({
  modules: state.moduleReducer.modules,
  course: state.courseReducer.course,
})

const propertyToDispatchMapper = (dispatch) => ({
  deleteModule: (module) => deleteModule(dispatch, module),
  createModule: (course, module) => createModule(dispatch, course, module),
  updateModule: (module) => updateModule(dispatch, module)
})
export default connect
(stateToPropertyMapper,
    propertyToDispatchMapper)
(ModuleList)
