import React from 'react';
import '../../node_modules/bootstrap/dist/css/bootstrap.css';
import '../../node_modules/font-awesome/css/font-awesome.min.css';
import CourseServiceClient from '../services/CourseService';
import CourseRow from "../components/CourseRowComponent";
//import '../styles/CourseList.css'
//import { confirmAlert } from 'react-confirm-alert'
//import 'react-confirm-alert/src/react-confirm-alert.css'

export default class CourseList extends React.Component {
  constructor() {
    super();
    this.courseServiceClient = CourseServiceClient.instance;
    this.state = {courses: []};
    this.titleChanged = this.titleChanged.bind(this);
    this.createCourse = this.createCourse.bind(this);
    this.deleteCourse = this.deleteCourse.bind(this);
  }

  componentDidMount() {
    this.findAllCourses();
  }

  findAllCourses() {
    this.courseServiceClient.findAllCourses()
    .then((courses) => {
      this.setState({courses: courses});
      console.log(courses);
    });
  }

  titleChanged(event) {
    this.setState({
      course: { title: event.target.value }
    });
  }

  createCourse() {
    console.log("creating course");
    this.courseServiceClient.createCourse(this.state.course)
    .then(() => { this.findAllCourses(); });
  }



  courseRows() {
    var rows = this.state.courses.map((course) => {
      return <CourseRow course={course} key={course.id} delete={this.deleteCourse}/>
    });
    return (
        rows
    )
  }

  render() {
    return (
        <div className="container-fluid">
          <div className="course-manager-title header">
            <i >2test</i>
            Course Manager
            <span>
            <input id="titleFld" placeholder="New Course Title" onChange={this.titleChanged}/>
            <i onClick={this.createCourse}>test</i>
          </span>
          </div>
          <table className="table">
            <thead>
            <tr>
              <th>Title</th>
              <th>Owned by</th>
              <th>Last modified by me</th>
            </tr>
            </thead>

            <tbody>
            {this.courseRows()}
            </tbody>
          </table>
        </div>
    )
  }
};
